import React, { useState, useEffect } from "react";
import image1 from "../Assests/p1.svg";
import "./Header.css";

const Header = () => {
  const [section, setSection] = useState("design");

  return (
    <div className="container-fluid mb-5">
      <div className="row">
        <div className="col-12">
          <nav>
            <button onClick={() => setSection("design")}>Design</button>
            <button onClick={() => setSection("development")}>
              Development
            </button>
            <button onClick={() => setSection("business")}>Business</button>
          </nav>
          <Section section={section} />
        </div>
      </div>
    </div>
  );
};

const Section = ({ section }) => {
  const [cards, setCards] = useState([]);

  useEffect(() => {
    if (section === "design") {
      setCards([
        { id: 1, image: image1, heading: "Design 1" },
        { id: 2, image: image1, heading: "Design 2" },
        { id: 3, image: image1, heading: "Design 3" },
      ]);
    } else if (section === "development") {
      setCards([
        {
          id: 1,
          image:
            "https://www.aihr.com/wp-content/uploads/organizational-development-examples-cover.png",
          heading: "Development 1",
        },
        {
          id: 2,
          image:
            "https://www.aihr.com/wp-content/uploads/organizational-development-examples-cover.png",
          heading: "Development 2",
        },
        {
          id: 3,
          image:
            "https://www.aihr.com/wp-content/uploads/organizational-development-examples-cover.png",
          heading: "Development 3",
        },
      ]);
    } else if (section === "business") {
      setCards([
        {
          id: 1,
          image:
            "data:image/jpeg;base64,",
          heading: "Business 1",
        },
        {
          id: 2,
          image:
            "data:image/jpeg;base64,",
          heading: "Business 2",
        },
        {
          id: 3,
          image:
            "data:image/jpeg;base64,",
          heading: "Business 3",
        },
      ]);
    }
  }, [section]);

  return (
    <div style={{ marginTop: "20px" }} className="container-fluid">
      <div className="row">
        <div className="col-12 card-col">
          {cards.map((card) => (
            <Card key={card.id} image={card.image} heading={card.heading} />
          ))}
        </div>
      </div>
    </div>
  );
};

const Card = ({ image, heading }) => {
  return (
    <div className="card">
      <img src={image} alt={heading} />
      <h3>{heading}</h3>
    </div>
  );
};

export default Header;
